import React from "react";

import Avatar from '@material-ui/core/Avatar';

import { makeStyles } from '@material-ui/core/styles';

import metallica from "../../data/mettalica";

import GeneralitesGroupRock from "../GeneralitesGroupRock/GeneralitesGroupRock";

import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Link from '@material-ui/core/Link';

const useStyles = makeStyles(theme => ({
    divAll: {
      display: 'flex',
      justifyContent: 'center',
      marginTop: 20,
      flexDirection: 'column'
    },
    divFlex: {
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center'
    }
}));

function ListAlign(items) {
  var list = '';
  items.map((item, key) => 
    list += item + ' - '
  );
  return list.substring(0, list.length - 3);
}

function dictionnary(key, value) {
  return { key, value };
}

const GroupRockDetail = () => {

  const classes = useStyles();

  let listeDesMembres = metallica.members;

  const dictionnaries = [
    dictionnary('Birthdate', metallica.lifeSpan.begin),
    dictionnary('Location', ListAlign(metallica.locationInfo)),
    dictionnary('Genre', ListAlign(metallica.genres)),
    dictionnary('Genre (DBpedia)', ListAlign(metallica.dbp_genre)),
    dictionnary('Label', ListAlign(metallica.labels)),
    dictionnary('Label (DBpedia)', ListAlign(metallica.recordLabel)),
    dictionnary('Number of Deezer fans', metallica.deezerFans),
    dictionnary('Associated artist(s)', null),
  ]

  const keyInfos = ['Birthdate', 'Location', 'Genre', 'Genre (DBpedia)', 'Label', 
    'Label (DBpedia)', 'Number of Deezer fans', 'Associated artist(s)'];
  const valueInfos = [ metallica.lifeSpan.begin, metallica.locationInfo, metallica.genres, 
    metallica.dbp_genre, metallica.labels, metallica.recordLabel, metallica.deezerFans, null ];

  let infosGenerales = [];
  for (var i = 0; i < keyInfos.length; i += 1) {
    const obj = {
      key: keyInfos[i],
      value: valueInfos[i]
    }
    infosGenerales.push(<GeneralitesGroupRock key={keyInfos[i]} value={valueInfos[i]} />);
  }


  return (
    <div className={classes.divAll}> 
        <div className={classes.divFlex}>
          <Avatar>{metallica.name[0]}</Avatar>
          <h1>{metallica.name}</h1>
        </div>

        <div>
          <p style={{
            marginLeft: 200,
            marginRight: 200,
