import React from "react";
import Single from "./Single";

function ListSingle(album) {
    let singleList = [];
    album.songs.map(item => (
        singleList.push(<Single single={item.title_accent_fold} />)
    ));
    return singleList;
}

function screenCover(cover) {
    let albumCover = '';
    if (cover != null) {
        albumCover = cover.medium;
    }

    <img class="imageAlbum style-scope x-album" alt={album.title}
          src={screenCover(album.cover)} />

    return albumCover;
}

const Album = ({ album }) => (
    <div style={{
        display: 'flex',
        flex: 1,
        marginTop: 20,
        marginLeft: 200,
        marginRight: 200,
        }}>

        <div>
          <img class="imageAlbum style-scope x-album" alt={album.title}
          src={screenCover(album.cover)} />
        </div>
        <div style={{
          maxHeight: 250,
          overflowY: 'scroll',
          overflowX: 'scroll',
          flex: 1,
          marginLeft: 10
        }}>
          <ol>
            {ListSingle(album)}
          </ol>
        </div>

    </div>
);

export default Album;
